mapper.select:
SELECT customer_id
  FROM CUSTOMER
 WHERE customer_id = #{userId}

SELECT salesman_id
  FROM SALESMAN
 WHERE salesman_id = #{userId}

SELECT customer_id userId, customer_nm userNm, email
  FROM CUSTOMER
 WHERE customer_id = #{userId}		 
   AND pwd         = #{password}

SELECT salesman_id userId, salesman_nm userNm, email, vendor_id vendorId, location vendorLoc
  FROM SALESMAN
 WHERE salesman_id = #{userId}	
   AND pwd         = #{password}
